import { useContextGlobal } from "./utils/global.context";
import { Link } from "react-router-dom";
import { routes } from "../routes";



const Card = ({ name, username, id }) => { 

  

  const addFav = ()=>{}
    // Aqui iria la logica para agregar la Card en el localStorage
    <div className="card">
      <div style={{display: 'flex',
flexWrap: 'wrap',
columnCount: 4,
columnGap: '10px',
rowGap: '10px',
marginTop: '10px',
justifyContent: 'center',
textAlign: 'center'
    }}>
{dentis.map( (dentis) => {
  return(

    <div key={dentis.id} style={{border: '1px solid black',
     marginBottom: '10px', display:'flex', flexWrap:'wrap',
     width: '30%' , height: '200px',alignItems: 'center'  ,
     justifyContent:'space-evenly', textAlign: 'center',
     flexDirection: 'column',backgroundColor: 'black', color:'white'}}>

    <h4 >{dentis.name}</h4>
    <h4 >{dentis.username}</h4>
    <h4 >{dentis.email}</h4>
    <h4 >{dentis.phone}</h4>
    <Link to={routes.details}><h4 style={{color: 'red'}}>{dentis.website}</h4></Link>
    <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  )}
  )}
  </div>
  
  
  </div>
  

// } )}
}
  
 
    
