import { createContext, useContext, useReducer } from "react";

const themes = {
  dark: {
      theme: true,
      bgColor: 'black',
      color: 'white'
  },
  light: {
      theme: false,
      bgColor: 'white',
      color: 'black'
  }
}

const initialApiState = []
const intialThemeState = themes.light


export const initialState = 
{
  theme: "",
  data: []
  }

export const ContextGlobal = createContext();

const themeReducer = (state,action) => {
    switch (action.type){
      case 'DARK': 
        return initialState.theme.dark
      case 'LIGTH':
        return initialState.theme.ligth

      default: 
      throw new Error()

    }
}

export const ContextProvider = ({ children }) => {

  const [themeState, themeDispatch] = useReducer(themeReducer, initialState)

  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  return (
    <ContextGlobal.Provider value={{themeState, themeDispatch}}>
      {children}
    </ContextGlobal.Provider>
  );
};

export default ContextProvider
export const useContextGlobal = () => useContext(ContextGlobal)
