import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import foto from '../images/doctor.jpg'



const Card = ({ name, username, id }) => {

  const [visible, setVisible] = useState(false)
  const [dentistState, setDentistState] = useState({});
  const navegar = useNavigate();
  const url = 'https://jsonplaceholder.typicode.com/users/' + id;

  useEffect(() => {
    fetch(url)
      .then(res => res.json())
      .then(data => setDentistState(data))
  }, [])

  const addFav = () => {
    setIsVisible(prev => false);
    // let fav = document.querySelector(".fav")
    let favs = JSON.parse(localStorage.getItem('favs')) || [];
    
    // if(favs) 
    // Verificar si el objeto ya existe en el array de favoritos
    const existingFav = favs.findIndex(fav => fav.id === dentistState.id);
    
    if (existingFav === -1) {
      
    const newFavs = [...favs, dentistState];
    localStorage.setItem('favs', JSON.stringify(newFavs));
    setIsVisible(prev => false);
      
     
  }else if (existingFav !== -1){

    // El objeto ya existe en favoritos, eliminarlo del array y guardar el nuevo array en localStorage
    const newFavs = favs.filter((fav) => fav.id !== dentistState.id);
    localStorage.setItem('favs', JSON.stringify(newFavs));
    setIsVisible(prev => false);
  }
  }
  let favs = JSON.parse(localStorage.getItem('favs')) || [];


  return(

    <div className="card" key={id} onClick={()=> navegar(`/details/${id}`)}>
    <p>{visible ? '❤️' : ''}</p>
    <img src={foto} alt="doctor" style={{width: '250px'}}/>
    <h4 >{name}</h4>
    <h4 >{username}</h4>
    <h4>{id}</h4>
    
    


    <button onClick={addFav} className="botonFav">
    Add fav ⭐
        </button>
    </div>
 
  )
}
export default Card;
  

