import { createContext, useContext, useReducer } from "react";


export const initialState = 
{
  theme: "",
  data: []
  }

export const ContextGlobal = createContext();

const themeReducer = (state,action) => {
    switch (action.type){
      case 'DARK': 
        return initialState.theme.dark
      case 'LIGTH':
        return initialState.theme.ligth

      default: 
      throw new Error()

    }
}

export const ContextProvider = ({ children }) => {

  const [theme, themeDispatch] = useReducer(themeReducer, initialState)

  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo

  return (
    <ContextGlobal.Provider value={{theme, themeDispatch}}>
      {children}
    </ContextGlobal.Provider>
  );
};

export default ContextProvider
export const useTheme = () => useContext(ContextGlobal)
