import doctor from "../images/doctor.jpg"
import { useNavigate } from "react-router-dom";



// import Card from "../Components/Card";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context


const borrarFav = () =>{
  if(localStorage)
  localStorage.clear()
}

const Favs = () => {
  let favs = localStorage.getItem('favs')
  let parsedFavs = JSON.parse(favs)
  

  const navegar = useNavigate()


  
  return (
    <>
      <h1>Dentists Favs</h1>
      <button className="eliminarFav" onClick={borrarFav}>Eliminar Favoritos</button>

      <div className="card-grid">
      
      
        {/* <div className="card"> */}
      {parsedFavs.map(dentis => (
          <div >
            
           <div className="card" key={dentis.id} onClick={()=> navegar(`/details/${dentis.id}`) }>
            <img className="imagen" src={doctor} alt="doctor" />
           <h2>{dentis.id}</h2>
           <h2>{dentis.name}</h2>
           <h2>{dentis.username}</h2>
             
      </div>
      </div>
            
       ))}
        {/* este componente debe consumir los destacados del localStorage */}
        {/* Deberan renderizar una Card por cada uno de ellos */}
      {/* </div> */}
      
      </div> 
    
    </>
  );
};

export default Favs;
