import  { useState } from 'react'
import { useEffect } from 'react'
import axios from "axios"
import { Link } from 'react-router-dom'
import { routes } from '../routes'




//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const [dentists, setDentists] = useState([])

  // const { id } = useParams ()

useEffect(()=>{
  // fetch('https://jsonplaceholder.typicode.com/users')
  // .then((response) => response.json())
  // .then((json) => console.log(json));

  axios.get('https://jsonplaceholder.typicode.com/users/')
  .then(res => setDentists(res.data))

}, [])

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  return (
    <>
      <h1>Detail Dentist id </h1>

      <div style={{display: 'flex',
  flexWrap: 'wrap',
  columnCount: 4,
  columnGap: '10px',
  rowGap: '10px',
  marginTop: '10px',
  justifyContent: 'center',
  textAlign: 'center'
          }}>
      {dentists.map( (dentis) => {
        return(

          <div key={dentis.id} style={{border: '1px solid black',
           marginBottom: '10px', display:'flex', flexWrap:'wrap',
           width: '30%' , height: '200px',alignItems: 'center'  ,
           justifyContent:'space-evenly', textAlign: 'center',
           flexDirection: 'column',backgroundColor: 'black', color:'white'}}>

          <h4 >{dentis.name}</h4>
          <h4 >{dentis.username}</h4>
          <h4 >{dentis.email}</h4>
          <h4 >{dentis.phone}</h4>
          <Link to={routes.details}><h4 style={{color: 'red'}}>{dentis.website}</h4></Link>
          <button style={{cursor: 'pointer', backgroundColor:'grey', width: '35%',
           borderRadius: '30px', border: 'none' }}>agregar a favoritos</button>
          </div>
          
        )
        

      } )}
      
      </div>

    
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
