    <h4>{id}</h4>
    <Link to={`/details/${id}`}><h4 style={{color: 'red'}}>Detail</h4></Link>
    


    <Button onClick={addFav} variant="contained" size="small">
    Add fav
        </Button>
    </div>
 
  )
}
