import  { useState } from 'react'
import { useEffect } from 'react'
import axios from "axios"
import { Link } from 'react-router-dom'
import { routes } from '../routes'




//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const [dentists, setDentists] = useState([])

useEffect(()=>{
  // fetch('https://jsonplaceholder.typicode.com/users')
  // .then((response) => response.json())
  // .then((json) => console.log(json));

  axios.get('https://jsonplaceholder.typicode.com/users')
  .then(res => setDentists(res.data))

}, [])

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  return (
    <>
      <h1>Detail Dentist id </h1>

      {dentists.map( (dentis) => {
        return(


          <div style={{display: 'flex', marginTop: '10px',
           justifyContent:'space-between', alignItems:'center'
          }}>
          <div key={dentis.id} style={{border: '1px solid black',
           marginBottom: '10px', display:'flex',
           width: '25%' , height: '200px',alignItems: 'center'  ,
           justifyContent:'space-evenly',
           flexDirection: 'column',backgroundColor: 'blue', color:'white'}}>

          <h4 style={{}}>{dentis.name}</h4>
          <h4>{dentis.username}</h4>
          <h4>{dentis.email}</h4>
          <h4>{dentis.phone}</h4>
          <Link to={routes.details}><h4>{dentis.website}</h4></Link>
                    
          </div>
          </div>
        )

      } )}
      


    
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
