import  { useState } from 'react'
import { useEffect } from 'react'
import axios from "axios"
import { Link } from 'react-router-dom'
import { routes } from '../routes'




//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const [dentists, setDentists] = useState([])

useEffect(()=>{
  // fetch('https://jsonplaceholder.typicode.com/users')
  // .then((response) => response.json())
  // .then((json) => console.log(json));

  axios.get('https://jsonplaceholder.typicode.com/users')
  .then(res => setDentists(res.data))

}, [])

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  return (
    <>
      <h1>Detail Dentist id </h1>

      {dentists.map( (dentis) => {
        return(
          <div style={{margin: '0', display: 'flex', flexDirection: 'row', backg}}>
          <div style={{ marginBottom: '10px',display:'flex', width: '25%' ,alignItems: 'center'  ,justifyContent:'space-evenly',flexDirection: 'column',backgroundColor: 'blue'}} key={dentis.id} >
          <h2>{dentis.name}</h2>
          <h2>{dentis.username}</h2>
          <h2>{dentis.email}</h2>
          <h2>{dentis.phone}</h2>
          <Link to={routes.details}><h3>{dentis.website}</h3></Link>

          
          
          </div>
          </div>
        )

      } )}
      


    
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
